<?phpnamespace App\Http\Controllers;use App\Http\Requests\MailRequest;use App\Mail\SendingMailToCustomer;use App\Mail\SendingPriceMailToMe;use Illuminate\Http\Request;use Illuminate\Support\Facades\Mail;use App\Mail\SendingMailToMe;use Illuminate\Support\Facades\Validator;class MailController extends Controller{    public function send(Request $request){        $validator = Validator::make($request->all(), [            'name' => 'required',            'email'=>'required|email',            'phone'=>'required',            'contentMail'=>'required'        ],[            'name.required' => 'Xin vui lòng nhập tên',            'phone.required' => 'Xin vui lòng nhập số điện thoại',            'email.required' => 'Xin vui lòng nhập email',            'email.email' => 'Xin vui lòng nhập đúng định dạng email',            'contentMail.required' => 'Xin vui lòng nhập nội dung cần tư vấn',        ]);        if ($validator->fails()) {            return response()->json([                'success' => false,                'validator'=>$validator->errors()            ]);        }        Mail::send(new SendingMailToMe());        Mail::send(new SendingMailToCustomer());        return response()->json([            'success' => true        ]);    }    public function sendPrice(Request $request){        $validator = Validator::make($request->all(), [            'name' => 'required',            'email'=>'required|email',            'phone'=>'required',        ],[            'name.required' => 'Xin vui lòng nhập tên',            'phone.required' => 'Xin vui lòng nhập số điện thoại',            'email.required' => 'Xin vui lòng nhập email',            'email.email' => 'Xin vui lòng nhập đúng định dạng email',        ]);        if ($validator->fails()) {            return response()->json([                'success' => false,                'validator'=>$validator->errors()            ]);        }        Mail::send(new SendingPriceMailToMe());        Mail::send(new SendingMailToCustomer());        return response()->json([            'success' => true        ]);    }}