<?phpnamespace App\Mail;use App\Config;use Illuminate\Bus\Queueable;use Illuminate\Mail\Mailable;use Illuminate\Queue\SerializesModels;use Illuminate\Contracts\Queue\ShouldQueue;use Illuminate\Http\Request;class SendingPriceMailToMe extends Mailable{    use Queueable, SerializesModels;    /**     * Create a new message instance.     *     * @return void     */    public function __construct()    {        //    }    /**     * Build the message.     *     * @return $this     */    public function build(Request $request)    {        $emailReceive = Config::where('name', 'email-receive')->first();        $subjectReceive = Config::where('name', 'email-receive-subject')->first();        $fromReceive = Config::where('name', 'email-receive-from')->first();        $idPrice = $request->type;        $content = '';        switch ($idPrice) {            case '1':                $content = 'Gói phim trường(basic)';                break;            case '2':                $content = 'Gói phim trường (vip)';                break;            case '3':                $content = 'Gói Đà Nẵng (basic)';                break;            case '4':                $content = 'Gói Đà Nẵng (vip)';                break;            case '5':                $content = 'Gói Đà Nẵng - Hội An (basic)';                break;            case '6':                $content = 'Gói Đà Nẵng - Hội An (vip)';                break;            case '7':                $content = 'Gói Đà Nẵng - Bà Nà (basic)';                break;            case '8':                $content = 'Gói Đà Nẵng - Bà Nà (vip)';                break;            case '9':                $content = 'Gói Đà Nẵng - Bà Nà - Hội An (basic)';                break;            case '10':                $content = 'Gói Đà Nẵng - Bà Nà - Hội An (vip)';                break;            case '11':                $content = 'Gói Đà Nẵng - Đồi Chè (basic)';                break;            case '12':                $content = 'Gói Đà Nẵng - Đồi Chè (vip)';                break;            case '13':                $content = 'Gói Đà Nẵng - Đèo Hải Vân - Lăng Cô (basic)';                break;            case '14':                $content = 'Gói Đà Nẵng - Đèo Hải Vân - Lăng Cô (vip)';                break;            case '15':                $content = 'Gói 4 Địa Điểm (Đà Nẵng, Hội An, Đồi Chè, Bà Nà, Lăng Cô) (basic)';                break;            case '16':                $content = 'Gói 4 Địa Điểm (Đà Nẵng, Hội An, Đồi Chè, Bà Nà, Lăng Cô) (vip)';                break;            case '17':                $content = 'Đảo Lý Sơn';                break;            case '18':                $content = 'Quy Nhơn';                break;            case '19':                $content = 'Đà Lạt';                break;            case '20':                $content = 'Nha Trang';                break;        }        return $this->view('mail.price-to-me', ['name' => $request->name, 'email' => $request->email, 'phone' => $request->phone, 'contentMail' => $request->contentMail, 'content' => $content])->to($emailReceive->content)->subject($subjectReceive->content)->from('nnduyquang@gmail.com', $fromReceive->content);;    }}